import React from 'react';
import { Text, View, TouchableOpacity, TextInput, Image, Alert, StyleSheet,KeyboardAvoidingView, ToastAndroid  } from 'react-native';
import * as Permissions from 'expo-permissions';
import { BarCodeScanner } from 'expo-barcode-scanner';
import firebase from 'firebase';
import db from '../config'

export default class TransactionScreen extends React.Component {
  constructor() {
    super();
    this.state = {
      hasCameraPermissions: null,
      scanned: false,
      scannedBookId: '',
      scannedStudentId: '',
      buttonState: 'normal',
      transactionMessage: null
    }
  }

  getCameraPermissions = async (id) => {
    const { status } = await Permissions.askAsync(Permissions.CAMERA);

    this.setState({
      /*status === "granted" is true when user has granted permission
        status === "granted" is false when user has not granted the permission
      */
      hasCameraPermissions: status === "granted",
      buttonState: id,
      scanned: false
    });
  }

  handleBarCodeScanned = async ({ type, data }) => {
    const { buttonState } = this.state

    if (buttonState === "BookId") {
      this.setState({
        scanned: true,
        scannedBookId: data,
        buttonState: 'normal'
      });
    }
    else if (buttonState === "StudentId") {
      this.setState({
        scanned: true,
        scannedStudentId: data,
        buttonState: 'normal'
      });
    }

  }



  handleTransaction = async()=>{
    var transactionMessage = null;
    db.collection("books").doc(this.state.scannedBookId).get()
    .then((doc)=>{
      var book = doc.data()
      if(book.bookAvailability){
        this.initiateBookIssue();
        transactionMessage = "Book Issued"
        // Alert.alert(transactionMessage)
        ToastAndroid.show(transactionMessage, ToastAndroid.SHORT);

      }
      else{
        this.initiateBookReturn();
        transactionMessage = "Book Returned"
        // Alert.alert(transactionMessage)
        ToastAndroid.show(transactionMessage, ToastAndroid.SHORT);

      }
    })

    this.setState({
      transactionMessage : transactionMessage
    })
  }



  initiateBookIssue = async ()=>{
    //add a transaction
    db.collection("transaction").add({
      studentId : this.state.scannedStudentId,
      bookId : this.state.scannedBookId,
      date : firebase.firestore.Timestamp.now().toDate(),
      transactionType : "Issue"
    })

    //change book status
    db.collection("books").doc(this.state.scannedBookId).update({
      bookAvailability : false
    })
    //change number of issued books for student
    db.collection("students").doc(this.state.scannedStudentId).update({
      noOfBooksIssued : firebase.firestore.FieldValue.increment(1)
    })

    this.setState({
      scannedStudentId : '',
      scannedBookId: '',
      transactionMessage:'Book Issued'
    })
    alert("Book Issued");
  }


  initiateBookReturn = async ()=>{
    //add a transaction
    db.collection("transaction").add({
      studentId : this.state.scannedStudentId,
      bookId : this.state.scannedBookId,
      date   : firebase.firestore.Timestamp.now().toDate(),
      transactionType : "Return"
    })

    //change book status
    db.collection("books").doc(this.state.scannedBookId).update({
      bookAvailability : true
    })

    //change book status
    db.collection("students").doc(this.state.scannedStudentId).update({
      noOfBooksIssued : firebase.firestore.FieldValue.increment(-1)
    })

    this.setState({
      scannedStudentId : '',
      scannedBookId : '',
      transactionMessage:'Book Returned'
    })
    alert("Book Returned");
  }

  render() {
    const hasCameraPermissions = this.state.hasCameraPermissions;
    const scanned = this.state.scanned;
    const buttonState = this.state.buttonState;

    if (buttonState !== "normal" && hasCameraPermissions) {
      return (
        <BarCodeScanner
          onBarCodeScanned={scanned ? undefined : this.handleBarCodeScanned}
          style={StyleSheet.absoluteFillObject}
        />
      );
    }

    else if (buttonState === "normal") {
      return (
        <KeyboardAvoidingView style={styles.container} behavior="padding" enabled>
          <View>
            <Image
              source={require("../assets/booklogo.jpg")}
              style={{ width: 200, height: 200 }} />
            <Text style={{ textAlign: 'center', fontSize: 30 }}>Wily</Text>
          </View>
          <View style={styles.inputView}>
            <TextInput
              style={styles.inputBox}
              placeholder="Book Id"
              onChangeText={text => this.setState({scannedBookId:text})}
              value={this.state.scannedBookId.toUpperCase()} 
              />
            <TouchableOpacity
              style={styles.scanButton}
              onPress={() => {
                this.getCameraPermissions("BookId")
              }}>
              <Text style={styles.buttonText}>Scan</Text>
            </TouchableOpacity>
          </View>
          <View style={styles.inputView}>
            <TextInput
              style={styles.inputBox}
              placeholder="Student Id"
              onChangeText={text => this.setState({scannedStudentId:text})}
              value={this.state.scannedStudentId.toUpperCase()} />
            <TouchableOpacity
              style={styles.scanButton}
              onPress={() => {
                this.getCameraPermissions("StudentId")
              }}>
              <Text style={styles.buttonText}>Scan</Text>
            </TouchableOpacity>
            </View>
            <TouchableOpacity
              style={styles.submitButton}
              onPress={async () => {
                var transactionMessage = await this.handleTransaction();
              }}>
              <Text style={styles.submitButtonText}>Submit</Text>

            </TouchableOpacity>
            <Text>{this.state.transactionMessage}</Text>


            </KeyboardAvoidingView>
      );
    }
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center'
  },
  displayText: {
    fontSize: 15,
    textDecorationLine: 'underline'
  },
  scanButton: {
    backgroundColor: '#2196F3',
    padding: 10,
    margin: 10
  },
  buttonText: {
    fontSize: 15,
    textAlign: 'center',
    marginTop: 10
  },
  inputView: {
    flexDirection: 'row',
    margin: 20
  },
  inputBox: {
    width: 200,
    height: 40,
    borderWidth: 1.5,
    borderRightWidth: 0,
    fontSize: 20
  },
  scanButton: {
    backgroundColor: '#66BB6A',
    width: 50,
    borderWidth: 1.5,
    borderLeftWidth: 0
  },
  submitButton: {
    backgroundColor: '#FBC02D',
    width: 100,
    height: 50
  },
  submitButtonText: {
    padding: 10,
    textAlign: 'center',
    fontSize: 20,
    fontWeight: "bold",
    color: 'white'
  }
});
